function timestampSort(a,b) {
    if (a.timestamp > b.timestamp) {
        return -1;
    }
    else if (a.timestamp < b.timestamp) {
        return 1;
    }
}


function threadTypeSort(a,b) {
    if (a.thread_type > b.thread_type) {
        return -1;
    }
    else if (a.thread_type < b.thread_type) {
        return 1;
    }
}

function prioritySort(a,b) {
    if (a.priority > b.priority) {
        return -1;
    }
    else if (a.priority < b.priority) {
        return 1;
    }
}

/* Obtain all of the messages from the XMPP server */
var xmpp = mdm.store.data__.xmpp.southeastern.history;
var messageId;

for(var i in xmpp){
	var messages = xmpp[i].messages;
	for (var j in messages){
		if (existingMsgIds.indexOf(messages[j].id) > -1){
			continue;
		}
		allMessagesArray.push(
			{
				"id": messages[j].id,
				"title": messages[j].metadata.title,
				"tagline": messages[j].metadata.tagline,
				"body": messages[j].body,
				"timestamp": messages[j].ts,
				"from": messages[j].from,
				"reply_type": messages[j].metadata.reply_type,
				"route": messages[j].metadata.route,
				"thread_id": messages[j].metadata.thread_id,
				"thread_type": messages[j].metadata.thread_type,
				"priority": messages[j].metadata.priority,
				"response_required": messages[j].metadata.response_required,
				"response_type": messages[j].metadata.response_type,
				"expired": messages[j].metadata.expired,
